body.script:
        const rssUrl = 'https://script.google.com/macros/s/AKfycbyfdWxqHRc9ur7KKpIz1j1UxbJaqB9uigdw-aeThe-Ynts0ISzVoV1icNYvLfT0XTjp8Q/exec';

        async function fetchRSS() {
            try {
                const response = await fetch(rssUrl);
                const data = await response.text();
                const parser = new DOMParser();
                const xmlDoc = parser.parseFromString(data, "text/xml");
                const items = xmlDoc.getElementsByTagName('item');
                const feedContainer = document.getElementById('rss-feed');

                for (let i = 0; i < items.length; i++) {
                    const title = items[i].getElementsByTagName('title')[0].textContent;
                    const link = items[i].getElementsByTagName('link')[0].textContent;
                    const description = items[i].getElementsByTagName('description')[0].textContent;

                    const itemDiv = document.createElement('div');
                    itemDiv.classList.add('feed-item');
                    itemDiv.innerHTML = `
${title}
${description}`;
                    feedContainer.appendChild(itemDiv);
                }
            } catch (error) {
                console.error('Fehler beim Abrufen des RSS-Feeds:', error);
            }
        }

        fetchRSS();
